(16, 1, '2013-02-07 14:05:58', '2013-02-07 14:05:58', '<div><span style="color: #000000; font-family: arial, helvetica, sans-serif;"><strong>Gestão 2013</strong></span></div>\r\n<div><span style="color: #000000; font-family: arial, helvetica, sans-serif;">\r\n</span></div>\r\n<div><span style="color: #000000; font-family: arial, helvetica, sans-serif;">Larissa Faria - Diretora Presidente.</span></div>\r\n<div><span style="color: #000000; font-family: arial, helvetica, sans-serif;"></span></div>\r\n<div><span style="font-family: arial, helvetica, sans-serif;"><span style="color: #000000;">Fernanda Castelhano de Souza - Diretora de Projetos e Vice-presidente.</span>\r\n</span></div>\r\n<div><span style="color: #000000; font-family: arial, helvetica, sans-serif;">\r\n</span></div>\r\n<div><span style="color: #000000; font-family: arial, helvetica, sans-serif;">Dwayne Becker Henkemaier - Diretora Administrativo-Financeiro.\r\n</span></div>\r\n<div><span style="color: #000000; font-family: arial, helvetica, sans-serif;">\r\n</span></div>\r\n<div><span style="color: #000000; font-family: arial, helvetica, sans-serif;">Flávia Schlichta - Diretora de Marketing.</span></div>\r\n<div><span style="color: #000000; font-family: arial, helvetica, sans-serif;">\r\n</span></div>\r\n<div><span style="color: #000000; font-family: arial, helvetica, sans-serif;">Aline Cardoso Hohenzollern da Rocha - Diretora de Gestão de Pessoas.</span></div>', 'Gestão', '', 'publish', 'open', 'open', '', 'gestao', '', '', '2013-03-05 17:28:03', '2013-03-05 17:28:03', '', 0, 'http://localhost/ecos/?page_id=16', 0, 'page', '', 0),
